def count_words(sentence):
    vowels = ['a', 'e', 'i', 'o', 'u']
    count = 1
    for word in sentence.split():
        if word[0].lower() not in vowels:
            has_vowel = any((char.lower() in vowels for char in word[1:]))
            if has_vowel:
                count += 1
    return count

