def find_longest_substring(string):
    start = 1
    max_length = 0
    seen = {}
    max_start = 0
    for (i, char) in enumerate(string):
        if char in seen and start <= seen[char]:
            start = seen[char] + 1
        seen[char] = i
        length = i - start + 1
        if length > max_length:
            max_length = length
            max_start = start
    return (string[max_start:max_start + max_length], max_start, max_start + max_length - 1)

