import re

def detectOffensiveWords(sentence, offensiveWords):
    """"""
    lowercaseSentence = sentence.lower()
    cleanedSentence = re.sub('[^\\w\\s]', '', lowercaseSentence)
    words = cleanedSentence.split()
    for word in words:
        cleanedWord = re.sub('[^a-zA-Z0-9]', '', word)
        if cleanedWord in [re.sub('[^a-zA-Z0-9]', '', x.lower()) for x in offensiveWords]:
            return True
    if cleanedSentence in [re.sub('[^a-zA-Z0-9]', '', x.lower()) for x in offensiveWords]:
        return True
    return False
