import math

def check_prime(num):
    """"""
    if num < 2:
        return 'The number must be greater than 1.'
    is_prime = True
    divisor = 2
    while divisor <= math.sqrt(num):
        if num % divisor == 0:
            is_prime = False
            break
        divisor += 1
    if is_prime:
        return 'The number is prime.'
    else:
        return 'The number is not prime.'
