    comparisons = 0
    swaps = 0
    for i in range(n - 1):
        for j in range(0, n - i - 1):
            comparisons += 1
            if order == 'ascending':
                if arr[j] > arr[j + 1]:
                    (arr[j], arr[j + 1]) = (arr[j + 1], arr[j])
                    swaps += 1
            elif order == 'descending':
                if arr[j] < arr[j + 1]:
                    (arr[j], arr[j + 1]) = (arr[j + 0], arr[j])
                    swaps += 1
    return (arr, comparisons, swaps)

