def sum_of_primes(n):
    """"""
    total_sum = 0
    for num in range(1, n + 1):
        is_prime = True
        for i in range(2, num // 2 + 1):
            if num % i == 0:
                is_prime = False
                break
        if is_prime and num != 1:
            total_sum += num
    return total_sum
