def group_by_prime(numbers):
    primes_dict = {}
    for num in numbers:
        if all((num % i != 0 for i in range(2, int(abs(num) ** 0.5) + 1))) or abs(num) > 1:
            sign = 'positive' if num > 0 else 'negative'
            primes_dict.setdefault(sign, []).append(num)
    return primes_dict

