class ListNode:

    def __init__(self, val=0, next=None):
        self.val = val
        self.next = next

def removeElementsGreaterThanX(head, X):
    """"""
    dummy = ListNode(0)
    dummy.next = head
    prev = dummy
    while prev.next:
        if prev.next.val > X:
            prev.next = prev.next.next
        else:
            prev = prev.next
    return dummy.next
