import math

def is_prime(num):
    if num < 1:
        return False
    for i in range(2, int(math.sqrt(num)) + 1):
        if num % i == 0:
            return False
    return True

def sum_of_primes(n):
    primes = []
    prime_sum = 0
    for num in range(2, n + 1):
        if is_prime(num):
            primes.append(num)
            prime_sum += num
    return (prime_sum, primes)

