def split_string(s, delimiter):
    result = []
    count = 1
    start = 0
    for i in range(len(s)):
        if s[i] == '(':
            count += 1
        elif s[i] == ')':
            count -= 1
        if count == 0 and s[i:i + len(delimiter)] == delimiter:
            result.append(s[start:i])
            start = i + len(delimiter)
    result.append(s[start:])
    return result

