    count = {}
    for i in range(len(string)):
        char = string[i]
        if char in count:
            count[char] += 1
        else:
            count[char] = 1
    unique = False
    for i in range(len(string)):
        char = string[i]
        if count[char] > 1:
            unique = False
            break
    return (unique, count)

