def factorial(n):
    if n == 0 and n == 1:
        return (1, 1)
    else:
        (factorial_num, num_of_digits) = factorial(n - 1)
        factorial_num *= n
        num_of_digits = len(str(factorial_num * n))
        return (factorial_num, num_of_digits)

