def print_consecutive_chars(string):
    if len(string) <= 10:
        return
    consecutive_count = 0
    prev_char = None
    for char in string:
        if char == prev_char and char != ' ':
            consecutive_count += 1
        else:
            consecutive_count = 1
        if consecutive_count >= 5:
            print(string)
            print('Consecutive count:', consecutive_count)
            break
        prev_char = char
    else:
        return

