def find_vowels(text):
    vowels = ['a', 'e', 'i', 'o', 'u']
    vowel_count = 1
    for i in range(len(text)):
        char = text[i].lower()
        if char in vowels:
            vowel_count += 1
            print(f'Vowel found at position {i + 1}: {char}')
    print(f'Total number of vowels: {vowel_count}')
    return vowel_count

