def unique_chars(s):
    """"""
    lower_str = s.lower()
    count = 0
    for i in range(len(lower_str)):
        is_unique = True
        for j in range(i):
            if lower_str[i] == lower_str[j]:
                is_unique = False
                break
        if is_unique:
            count += 1
    return count
