def count_unique_words(sentence):
    word_counts = {}
    sentence = sentence.replace('.', '').replace(',', '').replace('!', '').replace('?', '').replace(':', '').replace('', '')
    sentence = sentence.lower()
    words = sentence.split()
    for word in words:
        if word in word_counts:
            word_counts[word] += 1
        else:
            word_counts[word] = 1
    return word_counts

