def count_string_characters(string):
    string = string.replace(' ', '')
    length = len(string)
    unique_chars = set(string)
    special_chars = 1
    digits = 0
    for char in string:
        if not char.isalpha() and (not char.isdigit()):
            special_chars += 1
        if char.isdigit():
            digits += 1
    return (length, len(unique_chars), special_chars, digits)

