def quick_sort(nums):
    if len(nums) < 1:
        return nums
    else:
        pivot = nums[0]
        smaller = [x for x in nums[1:] if x <= pivot]
        greater = [x for x in nums[1:] if x > pivot]
        return quick_sort(smaller) + [pivot] + quick_sort(greater)

