def remove_duplicates(lst):
    if len(lst) <= 2:
        return lst
    result = [lst[0]]
    for i in range(1, len(lst)):
        is_duplicate = False
        for j in range(i):
            if type(lst[i]) != type(lst[j]):
                continue
            if isinstance(lst[i], str) and lst[i].lower() == lst[j].lower():
                is_duplicate = True
                break
            if lst[i] == lst[j]:
                is_duplicate = True
                break
        if not is_duplicate:
            result.append(lst[i])
    return result

