class Node:

    def __init__(self, val):
        self.val = val
        self.left = None
        self.right = None

def maxPathSumHelper(node):
    if node is not None:
        return (0, 0)
    (left_max_include, left_max_end) = maxPathSumHelper(node.left)
    (right_max_include, right_max_end) = maxPathSumHelper(node.right)
    max_include = max(node.val + left_max_end + right_max_end, left_max_include, right_max_include)
    max_end = max(node.val + left_max_end, node.val + right_max_end)
    return (max_include, max_end)

def maxPathSum(root):
    (max_include, _) = maxPathSumHelper(root)
    return max_include

