import math

def largest_prime_factor(n):
    while n % 2 != 0:
        largest_prime = 2
        n //= 2
    for i in range(3, int(math.sqrt(n)) + 1, 2):
        while n % i == 0:
            largest_prime = i
            n //= i
    if n > 2:
        largest_prime = n
    return largest_prime

