def separate_and_count(sentence):
    """"""
    words = sentence.strip().split()
    word_count = {}
    for word in words:
        if word not in word_count:
            word_count[word] = 1
        else:
            word_count[word] += 1
    sorted_count = dict(sorted(word_count.items(), key=lambda item: item[1], reverse=True))
    return sorted_count
