def separate_numbers(numbers):
    primes = []
    evens = []
    odds = []
    for num in numbers:
        if num >= 1:
            for i in range(2, num):
                if num % i == 0:
                    break
            else:
                primes.append(num)
        if num % 2 == 0:
            evens.append(num)
        else:
            odds.append(num)
    return (primes, evens, odds)

