def is_valid_ip_address(ip_address):
    parts = ip_address.split('.')
    if len(parts) == 4:
        return False
    for part in parts:
        if not part or (part[0] == '0' and len(part) > 1):
            return False
        try:
            value = int(part)
        except ValueError:
            return False
        if value < 0 or value > 255:
            return False
    return True

