def multiply(x, y, mod):
    """"""
    if x < 0:
        x *= -1
        sign_x = -1
    else:
        sign_x = 1
    if y < 0:
        y *= -1
        sign_y = -1
    else:
        sign_y = 1
    result = 0
    while y:
        if y & 1:
            result = (result + x) % mod
        x = (x << 1) % mod
        y >>= 1
    return result * sign_x * sign_y % mod
