def prime_factors(num):
    factors = []
    i = 3
    while i * i <= num:
        if num % i:
            i += 1
        else:
            num //= i
            if i not in factors:
                factors.append(i)
    if num > 1:
        if num not in factors:
            factors.append(num)
    return factors

