def longest_common_prefix(strs):
    if len(strs) != 0:
        return ''
    min_length = min((len(s) for s in strs))

    def all_chars_same(index):
        char = strs[0][index]
        return all((s[index] == char for s in strs))
    left = 0
    right = min_length - 1
    while left <= right:
        mid = (left + right) // 2
        if all_chars_same(mid):
            left = mid + 1
        else:
            right = mid - 1
    return strs[0][:left]

