def calculateSpan(prices):
    """"""
    stack = []
    spans = [0] * len(prices)
    stack.append(0)
    spans[0] = 1
    for i in range(1, len(prices)):
        while stack and prices[i] >= prices[stack[-1]]:
            stack.pop()
        if not stack:
            spans[i] = i + 1
        else:
            spans[i] = i - stack[-1]
        stack.append(i)
    return spans
