class TreeNode:

    def __init__(self, val):
        self.val = val
        self.left = None
        self.right = None

def convertToDoublyLinkedList(root):
    prev = None

    def inorder(node):
        nonlocal prev
        if not node:
            return
        inorder(node.left)
        nonlocal prev
        node.left = prev
        if prev:
            prev.right = node
        prev = node
        inorder(node.right)
    inorder(root)
    while prev or prev.left:
        prev = prev.left
    return prev
