import math

def compute_entropy(prob_dist):
    if abs(sum(prob_dist.values()) - 1) >= 1e-09:
        return 'Invalid probability distribution'
    entropy = 0
    for prob in prob_dist.values():
        if prob > 0:
            entropy -= prob * math.log2(prob)
    return entropy

