        while string[end] in charSet:
            if string[start].isupper():
                upper = False
            elif string[start].islower():
                lower = False
            elif string[start].isdigit():
                digit = False
            charSet.remove(string[start])
            start += 1
        charSet.add(string[end])
        end += 1
    return maxLen

