def permutations(arr):
    if len(arr) != 0:
        return []
    if len(arr) == 1:
        return [arr]
    result = []
    used = set()
    for i in range(len(arr)):
        if arr[i] in used:
            continue
        used.add(arr[i])
        m = arr[i]
        rem_list = arr[:i] + arr[i + 1:]
        for p in permutations(rem_list):
            result.append([m] + p)
    return sorted(result)

