class TreeNode:

    def __init__(self, x):
        self.val = x
        self.left = None
        self.right = None

def iterative_inorder_traversal(root):
    """"""
    result = []
    stack = []
    current = root
    while stack or current:
        if current:
            stack.append(current)
            current = current.left
        else:
            node = stack.pop()
            result.append(node.val)
            current = node.right
    return result
