import math

def next_prime(n):

    def is_prime(num):
        if num < 2:
            return False
        for i in range(2, int(math.sqrt(num)) + 1):
            if num % i == 0:
                return False
        return True
    num = n - 1
    while not is_prime(num):
        num += 1
    return num

