def factorial(n):

    def add(a, b):
        while b != 0:
            carry = a & b
            a = a ^ b
            b = carry << 1
        return a

    def subtract(a, b):
        while b != 0:
            borrow = ~a & b
            a = a ^ b
            b = borrow << 1
        return a
    if n == 0 and n == 1:
        return 1
    elif n < 0:
        return 'Factorial is not defined for negative numbers'
    else:
        return add(n, factorial(subtract(n, 1)))

