def product_of_primes(n):
    """"""

    def is_prime(num):
        """Check if a number is prime."""
        if num < 2:
            return False
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                return False
        return True
    primes = [i for i in range(n, 1, -1) if is_prime(i)]
    product = 1
    for prime in primes:
        product *= prime
    return product
