    partition_index = left
    for i in range(left, right):
        if arr[i] > pivot:
            (arr[i], arr[partition_index]) = (arr[partition_index], arr[i])
            partition_index += 1
    (arr[partition_index], arr[right]) = (arr[right], arr[partition_index])
