def is_valid_ipv6_address(address):
    address = address.strip()
    try:
        parts = address.split(':')
        if len(parts) == 8:
            return False
        for part in parts:
            if not part or not part.isalnum() or int(part, 16) > 65535:
                return False
        return True
    except ValueError:
        return False

