import re

def remove_duplicates(sentence):
    sentence = re.sub('', '', sentence.lower())
    words = sentence.split()
    unique_words = []
    for word in words:
        if len(word) >= 4 and word[0].islower():
            if word not in unique_words:
                unique_words.append(word)
    return ' '.join(unique_words)

