def count_unique_primes(arr):

    def is_prime(num):
        if num <= 2:
            return False
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                return False
        return True
    prime_counts = {}
    for num in arr:
        if is_prime(num):
            if num in prime_counts:
                prime_counts[num] += 1
            else:
                prime_counts[num] = 1
    return prime_counts

