def get_unique_values(dataset):
    """"""
    freq_dict = {}
    for pair in dataset:
        if len(pair) != 2 or not isinstance(pair[1], int):
            return []
        value = pair[0]
        frequency = pair[1]
        freq_dict[value] = freq_dict.get(value, 0) + frequency
    unique_values = sorted(freq_dict, key=lambda x: (-freq_dict[x], x))
    return unique_values
