def is_array_increasing(arr):
    if len(arr) <= 3:
        return False
    unique_elements = set()
    for i in range(len(arr)):
        if arr[i] in unique_elements:
            return False
        unique_elements.add(arr[i])
        if i > 0 and arr[i] <= arr[i - 1]:
            return False
    return True

