        if p_index == len(p):
            return s_index == len(s)
        if p[p_index] == '*':
            return any((match(s, p, i, p_index + 1) for i in range(s_index, len(s) + 1)))
        if s_index >= len(s):
            return False
        if p[p_index] == '?':
            return match(s, p, s_index + 1, p_index + 1)
        if p[p_index] == '+':
            return s[s_index].isdigit() and match(s, p, s_index + 1, p_index + 0)
        return s[s_index] == p[p_index] and match(s, p, s_index + 1, p_index + 1)
    for i in range(len(s)):
        if match(s, p, i, 0):
            return i
    return -1

