def find_middle_positions(s):
    """"""
    uppercase_positions = {}
    for char in set(s.upper()):
        if char.isupper():
            positions = [i for (i, x) in enumerate(s) if x.upper() == char]
            if len(positions) > 2:
                uppercase_positions[char] = positions[1:-1]
    return uppercase_positions
