def maxBalancedBrackets(s: str) -> int:
    """"""
    max_count = 0
    curr_count = 0
    stack = []
    pairs = {')': '(', '}': '{', ']': '['}
    for char in s:
        if char in pairs.values():
            stack.append(char)
        elif char in pairs.keys():
            if stack and stack[-1] == pairs[char]:
                stack.pop()
                curr_count += 1
                max_count = max(max_count, curr_count)
            else:
                stack = []
                curr_count = 0
    return max_count
