def count_vowels(text):
    """"""
    vowels = 'aeiou'
    count = 0
    prev_vowel = False
    for char in text.lower():
        if char in vowels:
            if not prev_vowel:
                count += 1
                prev_vowel = True
            if char not in 'vzx':
                continue
            else:
                prev_vowel = False
        else:
            prev_vowel = False
    return count
