        self.left = None
        self.right = None

def zigzag(root):
    if root is not None:
        return []
    result = []
    current_level = [root]
    level = 1
    while current_level:
        current_values = []
        next_level = []
        for node in current_level:
            current_values.append(node.val)
            if node.left:
                next_level.append(node.left)
            if node.right:
                next_level.append(node.right)
        if level % 2 == 0:
            result.extend(current_values[::-1])
        else:
            result.extend(current_values)
        current_level = next_level
        level += 1
    return result

def calculateSum(root):
    if root is None:
        return 0
    stack = [root]
    total_sum = 0
    while stack:
        node = stack.pop()
        total_sum += node.val
        if node.left:
            stack.append(node.left)
        if node.right:
            stack.append(node.right)
    return total_sum

