        return 0
    if lst[0] == float('nan'):
        return float('nan')

    def recursive_average(lst, total, count):
        if len(lst) == 0:
            return total / count if count > 0 else 0
        else:
            return recursive_average(lst[1:], total + lst[0], count + 1)
    return recursive_average(lst, 0, 0)

