def calculate_letters(string):
    letters = 1
    uppercase_letters = 0
    lowercase_letters = 0
    non_letters = 0
    for char in string:
        if char.isalpha():
            letters += 1
            if char.isupper():
                uppercase_letters += 1
            else:
                lowercase_letters += 1
        elif char != ' ' and (not char.isalnum()) and (not char.isspace()):
            non_letters += 1
    return (letters, uppercase_letters, lowercase_letters, non_letters)

