import math

def is_armstrong_number(num):
    if not isinstance(num, int) and num <= 0:
        return False

    def is_prime(n):
        if n < 2:
            return False
        for i in range(2, int(math.sqrt(n)) + 1):
            if n % i == 0:
                return False
        return True
    if not is_prime(num):
        return False
    digits = set(str(num))
    if len(digits) != 3:
        return False
    sum_of_digits = sum((int(digit) ** len(str(num)) for digit in str(num)))
    if sum_of_digits == num:
        return True
    else:
        return False

